import { Toaster } from "@/components/ui/toaster";
import { Toaster as Sonner } from "@/components/ui/sonner";
import { TooltipProvider } from "@/components/ui/tooltip";
import { QueryClient, QueryClientProvider } from "@tanstack/react-query";
import { HashRouter, Routes, Route } from "react-router-dom";
import { useState, useEffect } from "react";
import NDAGateway from "./components/NDAGateway";
import Navigation from "./components/Navigation";
import Index from "./pages/Index";
import OrderStock from "./pages/OrderStock";
import ProductionPlan from "./pages/ProductionPlan";
import IndirectMaterials from "./pages/IndirectMaterials";
import UtilityManagement from "./pages/UtilityManagement";
import FinishedProducts from "./pages/FinishedProducts";
import Certification from "./pages/Certification";
import Carbon from "./pages/Carbon";
import EnvironmentalMonitoring from "./pages/EnvironmentalMonitoring";
import Analytics from "./pages/Analytics";
import SupplierPortal from "./pages/SupplierPortal";
import Quality from "./pages/Quality";
import CostAnalysis from "./pages/CostAnalysis";
import PredictiveAnalytics from "./pages/PredictiveAnalytics";
import AdvancedReporting from "./pages/AdvancedReporting";
import MassBalanceDashboard from "./components/imbl/MassBalanceDashboard";
import CertificateGenerator from "./components/imbl/CertificateGenerator";
import IntegrationDashboard from "./components/isa95/IntegrationDashboard";
import EdgeCollectorDashboard from "./components/iot/EdgeCollectorDashboard";
import DigitalProductPassport from "./pages/DigitalProductPassport";
import AdvancedAnalytics from "./pages/AdvancedAnalytics";
import ProductionPlanForm from "./components/production/ProductionPlanForm";
import FinishedProductForm from "./components/products/FinishedProductForm";
import LLMApiSettings from "./components/settings/LLMApiSettings";
import AIReportGenerator from "./components/reports/AIReportGenerator";
import AICreditCalculator from "./components/credit/AICreditCalculator";
import NotFound from "./pages/NotFound";

const queryClient = new QueryClient();

const App = () => {
  const [hasConsent, setHasConsent] = useState<boolean | null>(null);

  useEffect(() => {
    const consent = sessionStorage.getItem('nda-consent');
    setHasConsent(consent === 'accepted');
  }, []);

  if (hasConsent === null) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="w-8 h-8 border-2 border-blue-600 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-gray-600">Loading...</p>
        </div>
      </div>
    );
  }

  if (!hasConsent) {
    return (
      <QueryClientProvider client={queryClient}>
        <TooltipProvider>
          <Toaster />
          <Sonner />
          <NDAGateway onAccept={() => setHasConsent(true)} />
        </TooltipProvider>
      </QueryClientProvider>
    );
  }

  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <Toaster />
        <Sonner />
        <HashRouter>
          <div className="min-h-screen bg-gray-50">
            <Navigation />
            <Routes>
              <Route path="/" element={<Index />} />
              <Route path="/order-stock" element={<OrderStock />} />
              <Route path="/production-plan" element={<ProductionPlan />} />
              <Route path="/indirect-materials" element={<IndirectMaterials />} />
              <Route path="/utility-management" element={<UtilityManagement />} />
              <Route path="/finished-products" element={<FinishedProducts />} />
              <Route path="/supplier-portal" element={<SupplierPortal />} />
              <Route path="/quality" element={<Quality />} />
              <Route path="/cost-analysis" element={<CostAnalysis />} />
              <Route path="/edge-collectors" element={<EdgeCollectorDashboard />} />
              <Route path="/predictive-analytics" element={<PredictiveAnalytics />} />
              <Route path="/advanced-reporting" element={<AdvancedReporting />} />
              <Route path="/certification" element={<Certification />} />
              <Route path="/carbon" element={<Carbon />} />
              <Route path="/environmental-monitoring" element={<EnvironmentalMonitoring />} />
              <Route path="/analytics" element={<Analytics />} />
              <Route path="/mass-balance" element={<MassBalanceDashboard />} />
              <Route path="/certificate-generator" element={<CertificateGenerator />} />
              <Route path="/isa95-integration" element={<IntegrationDashboard />} />
              <Route path="/digital-product-passport" element={<DigitalProductPassport />} />
              <Route path="/advanced-analytics" element={<AdvancedAnalytics />} />
              <Route path="/production-plan/new" element={<ProductionPlanForm />} />
              <Route path="/production-plan/edit/:id" element={<ProductionPlanForm />} />
              <Route path="/finished-products/new" element={<FinishedProductForm />} />
              <Route path="/finished-products/edit/:id" element={<FinishedProductForm />} />
              <Route path="/llm-settings" element={<LLMApiSettings />} />
              <Route path="/ai-report-generator" element={<AIReportGenerator />} />
              <Route path="/ai-credit-calculator" element={<AICreditCalculator />} />
              {/* ADD ALL CUSTOM ROUTES ABOVE THE CATCH-ALL "*" ROUTE */}
              <Route path="*" element={<NotFound />} />
            </Routes>
          </div>
        </HashRouter>
      </TooltipProvider>
    </QueryClientProvider>
  );
};

export default App;

import React, { useState, useEffect } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Checkbox } from "@/components/ui/checkbox";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Shield, Lock, FileText, Users } from "lucide-react";

const NDAGateway: React.FC<{ onAccept: () => void }> = ({ onAccept }) => {
  const [isChecked, setIsChecked] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    const consent = sessionStorage.getItem('nda-consent');
    if (consent === 'accepted') {
      onAccept();
    }
  }, [onAccept]);

  const handleAccept = () => {
    if (!isChecked) return;

    setIsSubmitting(true);
    sessionStorage.setItem('nda-consent', 'accepted');
    sessionStorage.setItem('nda-consent-timestamp', new Date().toISOString());

    setTimeout(() => {
      onAccept();
    }, 500);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 flex items-center justify-center p-4">
      <div className="w-full max-w-4xl">
        <div className="text-center mb-8">
          <div className="inline-flex items-center justify-center w-16 h-16 bg-blue-600 rounded-full mb-4">
            <Shield className="w-8 h-8 text-white" />
          </div>
          <h1 className="text-3xl font-bold text-slate-900 mb-2">Welcome to Our Partnership Portal</h1>
          <p className="text-slate-600">A collaborative platform for sustainable materials innovation</p>
        </div>

        <Card className="shadow-xl border-0">
          <CardHeader className="bg-slate-50 border-b">
            <CardTitle className="flex items-center gap-2 text-slate-900">
              <Users className="w-5 h-5" />
              Partnership Access Confirmation
            </CardTitle>
            <CardDescription>
              Please confirm your understanding of our collaborative system
            </CardDescription>
          </CardHeader>

          <CardContent className="p-8">
            <div className="space-y-6">
              <div className="bg-gradient-to-r from-blue-50 to-indigo-50 rounded-lg p-8 border border-blue-100">
              <div className="text-center space-y-4">
                <div className="inline-flex items-center justify-center w-12 h-12 bg-blue-100 rounded-full mb-4">
                  <Users className="w-6 h-6 text-blue-600" />
                </div>
                <h3 className="text-xl font-semibold text-slate-900">
                  Welcome to the ISCC+ Production Manager
                </h3>
                <p className="text-slate-600 max-w-md mx-auto">
                  This collaborative system is designed for partnership between
                  <strong> ShinMaywa USA, Inc.</strong> and <strong> Toray Industries, Inc.</strong>
                </p>
                <p className="text-sm text-slate-500">
                  Together we're working on sustainable materials production and ISCC+ certification management
                </p>
              </div>
            </div>

              <div className="bg-slate-50 rounded-lg p-6 border border-slate-200">
                <div className="flex items-start space-x-4">
                  <Checkbox
                    id="nda-accept"
                    checked={isChecked}
                    onCheckedChange={(checked) => setIsChecked(checked as boolean)}
                    className="mt-1"
                  />
                  <div className="flex-1 space-y-2">
                    <label
                      htmlFor="nda-accept"
                      className="text-base font-medium text-slate-900 cursor-pointer hover:text-blue-700 transition-colors"
                    >
                      I understand this is a shared system for our business partnership
                    </label>
                    <p className="text-sm text-slate-600 leading-relaxed">
                      I confirm that I'm an authorized user working on collaborative projects between
                      ShinMaywa USA, Inc. and Toray Industries, Inc. I'll use this system responsibly
                      for our joint business objectives.
                    </p>
                  </div>
                </div>
              </div>

              <div className="flex justify-center pt-4">
                <Button
                  onClick={handleAccept}
                  disabled={!isChecked || isSubmitting}
                  className="px-8 py-3 bg-blue-600 hover:bg-blue-700 text-white font-medium rounded-lg transition-all duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
                  size="lg"
                >
                  {isSubmitting ? (
                    <span className="flex items-center gap-2">
                      <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin" />
                      Processing...
                    </span>
                  ) : (
                    "Continue to Application"
                  )}
                </Button>
              </div>

              <div className="text-center pt-6 border-t border-slate-200">
                <p className="text-sm text-slate-600">
                  Thank you for being part of our partnership. Let's work together toward sustainable materials excellence.
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
};

export default NDAGateway;

