import React from 'react';

interface ResultPanelProps {
  results?: {
    success: boolean;
    ladderData: any;
    deviceMap: any;
    errors: string[];
    warnings: string[];
    processingTime: number;
  };
  showResults: boolean;
}

const ResultPanel: React.FC<ResultPanelProps> = ({ results, showResults }) => {
  if (!showResults || !results) {
    return null;
  }

  const { success, errors, warnings, processingTime, deviceMap } = results;

  return (
    <div className="bg-white rounded-lg shadow-sm border">
      <div className="p-6">
        <h3 className="text-lg font-medium text-gray-900 mb-4">å¤‰æ›çµæœ</h3>

        <div className="space-y-4">
          <div className="flex items-center justify-between">
            <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
              success
                ? 'bg-green-100 text-green-800'
                : 'bg-red-100 text-red-800'
            }`}>
              {success ? 'æˆåŠŸ' : 'å¤±æ•—'}
            </span>
            <span className="text-sm text-gray-500">
              å‡¦ç†æ™‚é–“: {processingTime.toFixed(2)}ç§’
            </span>
          </div>

          {errors.length > 0 && (
            <div className="border-l-4 border-red-400 bg-red-50 p-4">
              <div className="flex">
                <div className="ml-3">
                  <h4 className="text-sm font-medium text-red-800">ã‚¨ãƒ©ãƒ¼</h4>
                  <div className="mt-2 text-sm text-red-700">
                    <ul className="list-disc space-y-1 pl-5">
                      {errors.map((error, index) => (
                        <li key={index}>{error}</li>
                      ))}
                    </ul>
                  </div>
                </div>
              </div>
            </div>
          )}

          {warnings.length > 0 && (
