              ladderData={conversionResult?.ladderData}
              width={800}
              height={600}
            />

            <ResultPanel
              results={conversionResult}
              showResults={conversionResult !== null}
            />
          </div>
        </div>
      </main>
    </div>
  );
};

export default App;

