#!/usr/bin/env python3
"""
Cross-platform setup script for n8n-vc-kuka-beckhoff integration environment
"""

import sys
import subprocess
import os
import shutil
from pathlib import Path

def run_command(cmd, description, shell=False):
    """Run a command and handle errors"""
    print(f"\nðŸ”„ {description}...")
    try:
        result = subprocess.run(cmd, shell=shell, check=True, capture_output=True, text=True)
        print(f"âœ… {description} completed successfully")
        if result.stdout:
            print(result.stdout)
        return True
    except subprocess.CalledProcessError as e:
        print(f"âŒ {description} failed with error:")
        print(e.stderr)
        return False

def check_python_version():
    """Check if Python version is >= 3.8"""
    version = sys.version_info
    if version.major < 3 or (version.major == 3 and version.minor < 8):
        print(f"âŒ Python 3.8 or higher required. Found: {version.major}.{version.minor}.{version.micro}")
        return False
    print(f"âœ… Python version check passed: {version.major}.{version.minor}.{version.micro}")
    return True

def check_node_version():
    """Check if Node.js version is >= 18"""
    try:
        result = subprocess.run(['node', '--version'], capture_output=True, text=True, check=True)
        version_str = result.stdout.strip().lstrip('v')
        major = int(version_str.split('.')[0])
        if major < 18:
            print(f"âŒ Node.js 18 or higher required. Found: {version_str}")
            return False
        print(f"âœ… Node.js version check passed: {version_str}")
        return True
    except (subprocess.CalledProcessError, FileNotFoundError):
        print("âŒ Node.js not found or version check failed")
        return False

def create_venv():
    """Create virtual environment if it doesn't exist"""
    venv_path = Path(".venv")
    if venv_path.exists():
        print("âœ… Virtual environment already exists")
        return True

    print("Creating virtual environment...")
    try:
        subprocess.run([sys.executable, '-m', 'venv', '.venv'], check=True)
        print("âœ… Virtual environment created successfully")
        return True
    except subprocess.CalledProcessError:
        print("âŒ Failed to create virtual environment")
        return False

def install_python_deps():
    """Install Python dependencies from requirements.txt"""
    if not Path("requirements.txt").exists():
        print("âŒ requirements.txt not found")
        return False

    # Determine pip path based on OS
    if sys.platform == "win32":
        pip_path = ".venv\\Scripts\\pip"
    else:
        pip_path = ".venv/bin/pip"

    return run_command([pip_path, 'install', '-r', 'requirements.txt'], "Installing Python dependencies")

def install_node_deps():
    """Install Node.js dependencies"""
    if not Path("package.json").exists():
        print("âŒ package.json not found")
        return False

    return run_command(['npm', 'install'], "Installing Node.js dependencies")

def copy_env_template():
    """Copy .env.template to .env if .env doesn't exist"""
    env_path = Path(".env")
    template_path = Path(".env.template")

    if env_path.exists():
        print("âœ… .env file already exists")
        return True

    if not template_path.exists():
        print("âš ï¸  .env.template not found, skipping .env creation")
        return True

    try:
        shutil.copy2(template_path, env_path)
        print("âœ… Created .env file from .env.template")
        print("ðŸ“ Please edit .env file with your configuration")
        return True
    except Exception as e:
        print(f"âŒ Failed to copy .env.template to .env: {e}")
        return False

def main():
    """Main setup function"""
    print("ðŸš€ Setting up n8n-vc-kuka-beckhoff development environment...")

    # Check prerequisites
    if not check_python_version():
        return False

    if not check_node_version():
        return False

    # Setup environment
    if not create_venv():
        return False

    if not install_python_deps():
        return False

    if not install_node_deps():
        return False

    if not copy_env_template():
        return False

    print("\nðŸŽ‰ Development environment setup completed successfully!")
    print("\nNext steps:")
    print("1. Edit .env file with your configuration")
    print("2. Run 'npm run test:js' to test JavaScript components")
    print("3. Run 'python -m pytest' to test Python components")
    print("4. Run 'npm run n8n:start' to start n8n")

    return True

if __name__ == "__main__":
    success = main()
    sys.exit(0 if success else 1)

