// ãƒ†ã‚¹ãƒˆç”¨ã®ã‚¢ã‚µãƒ¼ã‚·ãƒ§ãƒ³é–¢æ•°
function assertEqual(actual, expected, message) {
  if (actual !== expected) {
    throw new Error(`${message}: Expected ${expected}, but got ${actual}`);
  }
}

function assertContains(text, substring, message) {
  if (!text.includes(substring)) {
    throw new Error(`${message}: Expected text to contain "${substring}"`);
  }
}

function assertGreaterThan(actual, expected, message) {
  if (actual <= expected) {
    throw new Error(`${message}: Expected ${actual} to be greater than ${expected}`);
  }
}

// FunctionãƒŽãƒ¼ãƒ‰ã®ã‚¹ã‚¯ãƒªãƒ—ãƒˆã‚’å®Ÿè¡Œã™ã‚‹ãŸã‚ã®ãƒ¢ãƒƒã‚¯ç’°å¢ƒ
function createMockInput(pathData) {
  return {
    item: {
      json: {
        path: pathData
      }
    }
  };
}

// ãƒ†ã‚¹ãƒˆå®Ÿè¡Œé–¢æ•°
function runKRLGeneratorTest(pathData) {
  // ãƒ¢ãƒƒã‚¯ç’°å¢ƒã®è¨­å®š
  const $input = createMockInput(pathData);

  // FunctionãƒŽãƒ¼ãƒ‰ã®ã‚¹ã‚¯ãƒªãƒ—ãƒˆã‚’å®Ÿè¡Œï¼ˆã“ã“ã§ã¯æ–‡å­—åˆ—ã¨ã—ã¦èª­ã¿è¾¼ã¿ã€evalã§å®Ÿè¡Œï¼‰
  // å®Ÿéš›ã®ãƒ†ã‚¹ãƒˆç’°å¢ƒã§ã¯ã€ã‚¹ã‚¯ãƒªãƒ—ãƒˆãƒ•ã‚¡ã‚¤ãƒ«ã‚’èª­ã¿è¾¼ã‚“ã§å®Ÿè¡Œ

  // KRLãƒ—ãƒ­ã‚°ãƒ©ãƒ ç”Ÿæˆãƒ­ã‚¸ãƒƒã‚¯ï¼ˆphase3_kuka/kuka_krl_generator.jsã®å†…å®¹ã‚’ã“ã“ã«å«ã‚ã‚‹ï¼‰
  const pathDataInput = $input.item.json.path;

  let krlProgram = '';

  // KRLãƒ—ãƒ­ã‚°ãƒ©ãƒ ãƒ˜ãƒƒãƒ€ãƒ¼
  krlProgram += '&ACCESS RVP\n';
  krlProgram += '&REL 1\n';
  krlProgram += '&PARAM TEMPLATE = C:\\KRC\\Roboter\\Template\\vorgabe\n';
  krlProgram += '&PARAM EDITMASK = *\n';
  krlProgram += 'DEF GENERATED_PROGRAM( )\n';
  krlProgram += '\n';

  // åˆæœŸåŒ–ã‚»ã‚¯ã‚·ãƒ§ãƒ³
  krlProgram += ';FOLD INI\n';
  krlProgram += '  ;FOLD BASISTECH INI\n';
  krlProgram += '    GLOBAL INTERRUPT DECL 3 WHEN $STOPMESS==TRUE DO IR_STOPM ( )\n';
  krlProgram += '    INTERRUPT ON 3\n';
  krlProgram += '    BAS (#INITMOV,0 )\n';
  krlProgram += '  ;ENDFOLD (BASISTECH INI)\n';
  krlProgram += '  ;FOLD USER INI\n';
  krlProgram += '    ;Make your modifications here\n';
  krlProgram += '  ;ENDFOLD (USER INI)\n';
  krlProgram += ';ENDFOLD (INI)\n';
  krlProgram += '\n';

  // ãƒ›ãƒ¼ãƒ ãƒã‚¸ã‚·ãƒ§ãƒ³ã¸ã®ç§»å‹•
  krlProgram += ';FOLD PTP HOME Vel=100 % DEFAULT\n';
  krlProgram += '  $BWDSTART = FALSE\n';
  krlProgram += '  PDAT_ACT = PDEFAULT\n';
  krlProgram += '  FDAT_ACT = FHOME\n';
  krlProgram += '  BAS (#PTP_DAT )\n';
  krlProgram += '  PTP {A1 0,A2 -90,A3 90,A4 0,A5 90,A6 0} C_PTP\n';
  krlProgram += ';ENDFOLD\n';
  krlProgram += '\n';

  // ãƒ‘ã‚¹ãƒ‡ãƒ¼ã‚¿ãŒå­˜åœ¨ã™ã‚‹å ´åˆã®å‡¦ç†
  if (pathDataInput && Array.isArray(pathDataInput) && pathDataInput.length > 0) {
    krlProgram += ';FOLD Generated Path Points\n';

    pathDataInput.forEach((point, index) => {
      if (point && typeof point.x !== 'undefined' && typeof point.y !== 'undefined' && typeof point.z !== 'undefined') {
        const pointNumber = index + 1;
        krlProgram += `  ;Point ${pointNumber}\n`;
        krlProgram += `  PTP {X ${point.x}, Y ${point.y}, Z ${point.z}, A 0, B 0, C 0} C_PTP\n`;

        if (index < pathDataInput.length - 1) {
          krlProgram += '  WAIT SEC 0.1\n';
        }
      }
    });

    krlProgram += ';ENDFOLD\n';
    krlProgram += '\n';
  } else {
    krlProgram += ';No path data provided\n';
    krlProgram += '\n';
  }

  // ãƒ›ãƒ¼ãƒ ãƒã‚¸ã‚·ãƒ§ãƒ³ã«æˆ»ã‚‹
  krlProgram += ';FOLD Return to HOME\n';
  krlProgram += '  PTP {A1 0,A2 -90,A3 90,A4 0,A5 90,A6 0} C_PTP\n';
  krlProgram += ';ENDFOLD\n';
  krlProgram += '\n';

  // ãƒ—ãƒ­ã‚°ãƒ©ãƒ çµ‚äº†
  krlProgram += 'END\n';

  // çµæžœã‚’è¿”ã™
  return [{ 
    json: { 
      krl_program: krlProgram,
      path_points_count: pathDataInput ? pathDataInput.length : 0,
      generated_timestamp: new Date().toISOString()
    } 
  }];
}

// ãƒ†ã‚¹ãƒˆã‚±ãƒ¼ã‚¹1: åŸºæœ¬çš„ãªKRLãƒ—ãƒ­ã‚°ãƒ©ãƒ ç”Ÿæˆãƒ†ã‚¹ãƒˆ
function testBasicKRLGeneration() {
  console.log('Running testBasicKRLGeneration...');

  const testPathData = [
    {x: 100, y: 0, z: 0},
    {x: 200, y: 50, z: 10}
  ];

  const result = runKRLGeneratorTest(testPathData);
  const krlProgram = result[0].json.krl_program;

  // åŸºæœ¬çš„ãªKRLãƒ—ãƒ­ã‚°ãƒ©ãƒ æ§‹é€ ã®ç¢ºèª
  assertContains(krlProgram, 'DEF GENERATED_PROGRAM( )', 'Should contain program definition');
  assertContains(krlProgram, 'BAS (#INITMOV,0 )', 'Should contain initialization');
  assertContains(krlProgram, 'PTP {A1 0,A2 -90,A3 90,A4 0,A5 90,A6 0} C_PTP', 'Should contain home position');
  assertContains(krlProgram, 'PTP {X 100, Y 0, Z 0, A 0, B 0, C 0} C_PTP', 'Should contain first path point');
  assertContains(krlProgram, 'PTP {X 200, Y 50, Z 10, A 0, B 0, C 0} C_PTP', 'Should contain second path point');
  assertContains(krlProgram, 'END', 'Should contain program end');

  // ãƒ‘ã‚¹ãƒã‚¤ãƒ³ãƒˆæ•°ã®ç¢ºèª
  assertEqual(result[0].json.path_points_count, 2, 'Should have correct path points count');

  console.log('testBasicKRLGeneration passed!');
}

// ãƒ†ã‚¹ãƒˆã‚±ãƒ¼ã‚¹2: ç©ºã®ãƒ‘ã‚¹ãƒ‡ãƒ¼ã‚¿ãƒ†ã‚¹ãƒˆ
function testEmptyPathData() {
  console.log('Running testEmptyPathData...');

  const result = runKRLGeneratorTest([]);
  const krlProgram = result[0].json.krl_program;

  // åŸºæœ¬çš„ãªKRLãƒ—ãƒ­ã‚°ãƒ©ãƒ æ§‹é€ ã¯å­˜åœ¨ã™ã‚‹ã¯ãš
  assertContains(krlProgram, 'DEF GENERATED_PROGRAM( )', 'Should contain program definition');
  assertContains(krlProgram, 'BAS (#INITMOV,0 )', 'Should contain initialization');
  assertContains(krlProgram, ';No path data provided', 'Should contain no path data comment');
  assertContains(krlProgram, 'END', 'Should contain program end');

  // ãƒ‘ã‚¹ãƒã‚¤ãƒ³ãƒˆæ•°ã®ç¢ºèª
  assertEqual(result[0].json.path_points_count, 0, 'Should have zero path points count');

